mapper.update:
UPDATE USER SET
    user_pw=#{userPw}
WHERE user_id=#{userId}

mapper.select:
SELECT user_no as userNo, user_id as userId, user_pw as userPw FROM USER
user_id like CONCAT('%',#{word},'%')
ORDER BY user_no DESC LIMIT #{pageHelper.startRow}, #{pageHelper.linePerPage}

SELECT * FROM boards
title like CONCAT('%', #{word}, '%')
order by id desc LIMIT #{pageHelper.startRow}, #{pageHelper.linePerPage}

SELECT *
FROM messages

recv_id = #{recv_id}  
	
		and title like CONCAT('%', #{word}, '%')
	

order by no desc
LIMIT #{pageHelper.startRow}, #{pageHelper.linePerPage}

SELECT *
FROM messages

send_id = #{send_id}  
	
		and title like CONCAT('%', #{word}, '%')
	

order by no desc
LIMIT #{pageHelper.startRow}, #{pageHelper.linePerPage}

mapper.insert:
INSERT INTO
USER(
    user_id,
    user_pw
) VALUES(
    #{userId},
    #{userPw}
)
